import React, { useState, useCallback } from "react";
import PropTypes from "prop-types";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Paper from "@material-ui/core/Paper";
import Divider from "@material-ui/core/Divider";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";
import Grid from "@material-ui/core/Grid";
import LoginForm from "./LoginForm";
import UserProfile from "./UserProfile";
import Link from "next/link";
import HomeIcon from "@material-ui/icons/Home";
import { useSelector } from "react-redux";
import Router from "next/router";
import styles from "../styles/Applayout.module.scss";

const AppLayout = ({ children }) => {
  const { me } = useSelector((state) => state.user);
  const [search, setSearch] = useState("");
  // const [logInDone, setlogInDone] = useState(false);
  // const classes = useStyles();
  const searchChange = useCallback(
    (e) => {
      setSearch(e.target.value);
    },
    [search]
  );
  const searchBtn = useCallback(() => {
    Router.push(`/hashtag/${search}`);
  }, [search]);

  return (
    <>
      {me ? (
        <div>
          <div className={styles.wrap}>
            <div className={styles.logo}>Logo</div>
            <div>
              <Paper component="form" className={styles.searchInputContainer}>
                <input
                  className={styles.searchInputStyle}
                  style={{ border: "none" }}
                  placeholder="검색"
                  value={search}
                  onChange={searchChange}
                />

                <Divider
                  style={{ height: 20, margin: "auto" }}
                  orientation="vertical"
                />
                <Button onClick={searchBtn}>
                  <SearchIcon />
                </Button>
              </Paper>
            </div>
            <div className={styles.textLayout}>
              <Link href="/">
                <a>
                  <HomeIcon className={styles.homeIcon} />
                  {/* <br />홈 */}
                </a>
              </Link>
            </div>
            {me && (
              <Grid item className={styles.textLayout}>
                <Link href="/profile">
                  <a>
                    <FavoriteIcon /> <br />
                    프로필
                  </a>
                </Link>
              </Grid>
            )}
            {/* {!me && (
          <Grid item className={styles.textLayout}>
            <Link href="/signup">
              <a>
                <PersonIcon /> <br />
                회원가입
              </a>
            </Link>
          </Grid>
        )} */}
          </div>
          <Grid
            container
            maxwidth="xs"
            item
            justifyContent="center"
            spacing={3}
          >
            <Grid item xs={12} md={12}>
              {me ? <UserProfile /> : <LoginForm />}
            </Grid>
            <Grid item xs={6} md={6}>
              {children}
            </Grid>
            <Grid item xs={12} md={12}>
              <a
                href="https://www.github.com/6810779s"
                target="_blank"
                rel="noreferrer noopener"
              >
                Made by eunhee
              </a>
            </Grid>
          </Grid>
        </div>
      ) : (
        <div className={styles.loginContainer}>
          <div className={styles.logo}>
            <h3>LifeTory</h3>
            <div >추억을 </div>
          </div>
          <LoginForm />
        </div>
      )}
    </>
  );
};

//xs:모바일, sm:태블릿, md:작은 데스크탑, lg:큰 데스크탑
AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
