import React, { useState, useCallback } from "react";

export default (initialValue = null) => {
  const [data, setData] = useState(initialValue);
  console.log("data:", data);
  const handler = useCallback(
    (e) => {
      const { value, name } = e.target;
      setData({
        ...data,
        [name]: value,
      });
    },
    [data]
  );
  console.log("data:", data);
  console.log("data:", data);
  return [data, handler];
};
