import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import AppLayout from "../components/AppLayout";
import Loading from "../components/Loading";
import PostCard from "../components/PostCard";
import PostForm from "../components/PostForm";
import { LOAD_POST_REQUEST } from "../constants/post";
import { LOAD_MY_INFO_REQUEST } from "../constants/user";
import { useInView } from "react-intersection-observer";
import wrapper from "../store/configureStore";
import { END } from "redux-saga";

const Home = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const { mainPosts, hasMorePosts, loadPostLoading, retweetFailure } =
    useSelector((state) => state.post);
  const [ref, inView] = useInView({
    threshold: 1,
    triggerOnce: true,
    unobserveOnEnter: true,
    delay: 1000,
    trackVisibility: true,
  });

  useEffect(() => {
    if (retweetFailure) {
      alert(retweetFailure);
    }
  }, [retweetFailure]);

  // useEffect(() => {
  //   dispatch({ type: LOAD_MY_INFO_REQUEST });
  //   dispatch({
  //     type: LOAD_POST_REQUEST,
  //   });
  // }, []);

  useEffect(() => {
    if (inView && !loadPostLoading && hasMorePosts) {
      const lastId = mainPosts[mainPosts.length - 1]?.id;
      dispatch({
        type: LOAD_POST_REQUEST,
        lastId,
      });
    }
  }, [inView]);

  return (
    <AppLayout>
      {me && <PostForm />}
      {mainPosts.map((post) => (
        // <PostCard post={post} key={post.id} />
        <React.Fragment key={post.id}>
          <PostCard post={post} />
          {post.id === mainPosts[mainPosts.length - 1]?.id && hasMorePosts ? (
            <div ref={ref}>
              <Loading />
            </div>
          ) : (
            ""
          )}
        </React.Fragment>
      ))}
    </AppLayout>
  );
};

export const getServerSideProps = wrapper.getServerSideProps(
  async (context) => {
    context.store.dispatch({ type: LOAD_MY_INFO_REQUEST });
    context.store.dispatch({
      type: LOAD_POST_REQUEST,
    });
    context.store.dispatch(END);
    await context.store.sagaTask.toPromise();
  }
);

export default Home;
