import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import AppLayout from "../components/AppLayout";
import Loading from "../components/Loading";
import PostCard from "../components/PostCard";
import PostForm from "../components/PostForm";
import { LOAD_POST_REQUEST } from "../constants/post";
import { LOAD_MY_INFO_REQUEST } from "../constants/user";
import { useInView } from "react-intersection-observer";
import { Container } from "@material-ui/core";

const Home = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const { mainPosts, hasMorePosts, loadPostLoading, retweetFailure } =
    useSelector((state) => state.post);
  const [ref, inView] = useInView();

  useEffect(() => {
    if (retweetFailure) {
      alert(retweetFailure);
    }
  }, [retweetFailure]);

  useEffect(() => {
    dispatch({ type: LOAD_MY_INFO_REQUEST });
    dispatch({
      type: LOAD_POST_REQUEST,
    });
  }, []);
  console.log("hasMorePosts", hasMorePosts);
  console.log("mainPosts", mainPosts);

  useEffect(() => {
    if (inView && !loadPostLoading && hasMorePosts) {
      const lastId = mainPosts[mainPosts.length - 1]?.id;
      console.log("lastId", lastId);
      dispatch({
        type: LOAD_POST_REQUEST,
        lastId,
      });
    }
  }, [inView, mainPosts]);

  return (
    <AppLayout>
      {me && <PostForm />}
      {mainPosts.map((post) =>
        post.id === mainPosts[mainPosts.length - 1]?.id ? (
          <PostCard key={post.id} post={post} />
        ) : (
          <PostCard key={post.id} post={post} innerRef={ref} />
        )
      )}
      <div>{hasMorePosts && <Loading />}</div>
    </AppLayout>
  );
};

export default Home;
