import React from "react";
import { Button, InputBase, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/styles";
import { useDispatch } from "react-redux";
import { CHANGE_NICKNAME_REQUEST } from "../constants/user";

const useStyles = makeStyles({
  paperWrap: { display: "flex" },
  input: { flex: 1 },
});
const NickNameEditForm = () => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const changeNickName = () => {
    dispatch({ type: CHANGE_NICKNAME_REQUEST });
  };
  return (
    <Paper className={classes.paperWrap}>
      <InputBase placeholder="닉네임" className={classes.input} />
      <Button variant="contained" color="primary" onClick={changeNickName}>
        수정
      </Button>
    </Paper>
  );
};

export default NickNameEditForm;
