import React, { useState } from "react";
import { Button, Input, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/styles";
import { useDispatch } from "react-redux";
import { CHANGE_NICKNAME_REQUEST } from "../constants/user";

const useStyles = makeStyles({
  paperWrap: { display: "flex" },
  input: { flex: 1 },
});
const NickNameEditForm = () => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const [nickname, setNickname] = useState("");
  const changeNickName = (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    dispatch({ type: CHANGE_NICKNAME_REQUEST, data: nickname });
  };
  return (
    <Paper
      className={classes.paperWrap}
      component="form"
      onSubmit={changeNickName}
    >
      <Input placeholder="닉네임" id="nickname" nameclassName={classes.input} />
      <Button variant="contained" type="submit">
        수정
      </Button>
    </Paper>
  );
};

export default NickNameEditForm;
