import React, { useState, useCallback } from "react";
import PropTypes from "prop-types";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Paper from "@material-ui/core/Paper";
import Divider from "@material-ui/core/Divider";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";
import Grid from "@material-ui/core/Grid";
import LoginForm from "./LoginForm";
import UserProfile from "./UserProfile";
import Link from "next/link";
import HomeIcon from "@material-ui/icons/Home";
import { useSelector } from "react-redux";
import Router from "next/router";
import styles from "../styles/Applayout.module.scss";
import { Avatar } from "@material-ui/core";

const AppLayout = ({ children }) => {
  const { me } = useSelector((state) => state.user);
  const [search, setSearch] = useState("");
  // const [logInDone, setlogInDone] = useState(false);
  // const classes = useStyles();
  const searchChange = useCallback(
    (e) => {
      setSearch(e.target.value);
    },
    [search]
  );
  const searchBtn = useCallback(() => {
    Router.push(`/hashtag/${search}`);
  }, [search]);

  return (
    <>
      {me ? (
        <div className={styles.container}>
          <div className={styles.menuContainer}>
            <div className={styles.wrap}>
              <div className={styles.logo}>LifeTory</div>
              <div>
                <Paper component="form" className={styles.searchInputContainer}>
                  <input
                    className={styles.searchInputStyle}
                    style={{ border: "none" }}
                    placeholder="검색"
                    value={search}
                    onChange={searchChange}
                  />

                  <Divider
                    style={{ height: 20, margin: "auto" }}
                    orientation="vertical"
                  />
                  <Button onClick={searchBtn}>
                    <SearchIcon />
                  </Button>
                </Paper>
              </div>
              <div className={styles.menu}>
                <div className={styles.textLayout}>
                  <Link href="/">
                    <a>
                      <HomeIcon className={styles.homeIcon} />
                      {/* <br />홈 */}
                    </a>
                  </Link>
                </div>
                <div>
                  <Link href="/profile">
                    <a>
                      <Avatar>{me.nickname[0]}</Avatar>
                    </a>
                  </Link>
                </div>
              </div>
            </div>
          </div>
          <div
            container
            maxwidth="xs"
            item
            justifyContent="center"
            spacing={3}
            className={styles.mainContainer}
          >
            <Grid item xs={12} md={12}>
              <UserProfile />
            </Grid>
            <Grid item xs={6} md={6}>
              {children}
            </Grid>
            <Grid item xs={12} md={12}>
              <a
                href="https://www.github.com/6810779s"
                target="_blank"
                rel="noreferrer noopener"
              >
                Made by eunhee
              </a>
            </Grid>
          </div>
        </div>
      ) : (
        <LoginForm />
      )}
    </>
  );
};

//xs:모바일, sm:태블릿, md:작은 데스크탑, lg:큰 데스크탑
AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
