import React, { useEffect } from "react";
import AppLayout from "../components/AppLayout";
import Head from "next/head";
import NickNameEditForm from "../components/NickNameEditForm";
import FollowList from "../components/FollowList";
import { useDispatch, useSelector } from "react-redux";
import Router from "next/router";
import useSWR from "swr";
import {
  LOAD_FOLLOWERS_REQUEST,
  LOAD_FOLLOWINGS_REQUEST,
} from "../constants/user";
import axios from "axios";
const fetcher = (url) =>
  axios.get(url, { withCredentials: true }).then((result) => result.data);
const profile = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const [followersLimit, setFollowersLimit] = useState();
  const [followingsLimit, setfollowingsLimit] = useState();
  const { data: followersData, error: followerError } = useSWR(
    `http://localhost:3065/user/followers?limit=${followersLimit}`,
    fetcher
  );
  const { data: followingsData, error: followingError } = useSWR(
    `http://localhost:3065/user/followingslimit=${followingsLimit}`,
    fetcher
  );

  // useEffect(() => {
  //   dispatch({ type: LOAD_FOLLOWERS_REQUEST });
  //   dispatch({ type: LOAD_FOLLOWINGS_REQUEST });
  // }, []);
  useEffect(() => {
    if (!(me && me.id)) {
      Router.push("/");
    }
  }, [me && me.id]);
  if (!me) {
    return "내정보 로딩중...";
  }

  if (followerError || followingError) {
    console.error(followerError || followingError);
    return <div>팔로워/팔로잉중 에러가 발생합니다.</div>;
  }
  return (
    <>
      <Head>
        <title>profile | nodeBird</title>
      </Head>
      <AppLayout>
        <NickNameEditForm />
        <FollowList header="팔로잉 목록" data={followersData} />
        <FollowList header="팔로워 목록" data={followingsData} />
      </AppLayout>
    </>
  );
};

export default profile;
