import { Box, Button, Input, TextareaAutosize } from "@material-ui/core";
import React, { useCallback, useState, useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  ADD_POST_REQUEST,
  REMOVE_IMAGE,
  UPLOAD_IMAGES_REQUEST,
} from "../constants/post";
import { addPost } from "../reducers/post";

const PostForm = () => {
  const dispatch = useDispatch();
  //   const imageInput = useRef();
  const [textAreaValue, setTextAreaValue] = useState("");
  const onChangeText = useCallback((e) => {
    setTextAreaValue(e.target.value);
  }, []);
  const { imagePaths, addPostDone } = useSelector((state) => state.post);

  useEffect(() => {
    if (addPostDone) {
      setTextAreaValue("");
    }
  }, [addPostDone]);
  const imageInput = useRef();

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData(e.currentTarget);
    const formData = new FormData();
    imagePaths.forEach((p) => {
      formData.append("image", i);
    });
    formData.append("content", data.get("dailyText"));
    if (data.get("dailyText") !== "") {
      dispatch({ type: ADD_POST_REQUEST, data: formData });
    }

  };

  const onChangeImage = useCallback((e) => {
    console.log("images:", e.target.files);
    const imageFormData = new FormData();

    [].forEach.call(e.target.files, (f) => {
      imageFormData.append("image", f);
    });
    dispatch({ type: UPLOAD_IMAGES_REQUEST, data: imageFormData });
  });

  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);

  const onRemoveImage = useCallback((index) => () => {
    dispatch({ type: REMOVE_IMAGE, data: index });
  });

  return (
    <Box component="form" noValidate onSubmit={handleSubmit}>
      <TextareaAutosize
        name="dailyText"
        id="dailyText"
        aria-label="minimum height"
        value={textAreaValue}
        onChange={onChangeText}
        minRows={5}
        placeholder="어떤 신기한 일이 있었나요?"
        autoFocus
        style={{ width: "100%" }}
      />
      {/* <Input accept="image/*" multiple type="file" /> */}
      <div>
        <input
          type="file"
          ref={imageInput}
          name="image"
          multiple
          hidden
          onChange={onChangeImage}
        />
        <Button onClick={onClickImageUpload} variant="contained">
          이미지 업로드
        </Button>
      </div>
      <Button
        type="submit"
        fullWidth
        variant="contained"
        style={{ marginTop: "10px" }}
      >
        짹짹
      </Button>
      <div>
        {imagePaths.map((v, i) => (
          <div key={v} style={{ display: "inline-block" }}>
            <img
              src={`http://localhost:3065/${v}`}
              style={{ width: "200px" }}
              alt={v}
            />
            <div>
              <Button onClick={onRemoveImage(i)}>제거</Button>
            </div>
          </div>
        ))}
      </div>
    </Box>
  );
};

export default PostForm;
