import React, { useCallback, useState, useRef } from "react";
import PropTypes from "prop-types";
import AddIcon from "@material-ui/icons/Add";
import ImagesZoom from "./imagesZoom";
import styles from "../styles/PostImages.module.scss";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
const PostImages = ({ images }) => {
  const [showImageszoom, setShowImageszoom] = useState(false);
  const [slideNum, setSlideNum] = useState(0);
  const imgContainer = useRef(null);
  const prevBtn = useRef(null);
  const nextBtn = useRef(null);
  const onZoom = useCallback(() => {
    setShowImageszoom(true);
  }, []);
  const onClose = useCallback(() => {
    setShowImageszoom(false);
  }, []);

  const moveSlide = (num) => {
    imgContainer.current.style.left = num * 470 + `px`;
  };

  const prevBtnClick = useCallback(() => {
    if (imgContainer.current) {
      imgContainer.current.style.transform = `translateX(470px)`;
      setSlideNum(slideNum - 1);
    }
  }, [slideNum]);

  const nextBtnClick = useCallback(() => {
    if (imgContainer.current) {
      if (slideNum < images.length) {
        imgContainer.current.style.left = slideNum * 470 + `px`;
        setSlideNum(slideNum + 1);
      }
    }
    console.log("images:", images.length);
  }, [slideNum]);
  console.log("slideNum:", slideNum);
  const imagesReturn = useCallback(() => {
    const containerWidth = 470 * images.length;

    if (imgContainer.current && prevBtn.current && nextBtn.current) {
      imgContainer.current.style.width = `${containerWidth}px`;
      if (slideNum == 0) {
        prevBtn.current.style.display = "none";
      } else if (slideNum == images.length - 1) {
        nextBtn.current.style.display = "none";
      } else {
        prevBtn.current.style.display = "block";
        nextBtn.current.style.display = "block";
      }
    }
    return (
      <div className={styles.imgContainer} ref={imgContainer}>
        {images.map((v) => (
          <img
            role="presentation"
            src={`http://localhost:3065/${v.src}`}
            alt={v.src}
            onClick={onZoom}
          />
        ))}
      </div>
    );
  }, [imgContainer && prevBtn && nextBtn, slideNum]);

  if (images.length === 1) {
    return (
      <div className={styles.container}>
        <img
          role="presentation"
          src={`http://localhost:3065/${images[0].src}`}
          alt={images[0].src}
          onClick={onZoom}
        />
        {showImageszoom && <ImagesZoom images={images} onClose={onClose} />}
      </div>
    );
  }

  // else if (images.length === 2) {
  //   return (
  //     <>
  //       {" "}
  //       <img
  //         role="presentation"
  //         width="50%"
  //         src={`http://localhost:3065/${images[0].src}`}
  //         alt={images[0].src}
  //         onClick={onZoom}
  //       />
  //       <img
  //         role="presentation"
  //         width="50%"
  //         src={`http://localhost:3065/${images[1].src}`}
  //         alt={images[1].src}
  //         onClick={onZoom}
  //       />
  //       {showImageszoom && <ImagesZoom images={images} onClose={onClose} />}
  //     </>
  //   );
  // }
  return (
    <div className={styles.slideContainer}>
      <NavigateBeforeIcon
        className={styles.backIcon}
        ref={prevBtn}
        onClick={prevBtnClick}
      />
      <NavigateNextIcon
        className={styles.forwardIcon}
        ref={nextBtn}
        onClick={nextBtnClick}
      />
      {imagesReturn()}
    </div>
    // <>
    //   <div style={{ display: "flex", alignItems: "center" }}>
    //     <img
    //       role="presentation"
    //       width="50%"
    //       src={`http://localhost:3065/${images[0].src}`}
    //       alt={images[0].src}
    //       onClick={onZoom}
    //     />
    //     <div
    //       role="presentation"
    //       style={{
    //         width: "50%",
    //         textAlign: "center",
    //       }}
    //       onClick={onZoom}
    //     >
    //       <AddIcon style={{ fontSize: "50px", cursor: "pointer" }} />
    //       <br />
    //       {images.length - 1} 개의 사진 더보기
    //     </div>
    //   </div>
    //   {showImageszoom && <ImagesZoom images={images} onClose={onClose} />}
    // </>
  );
};

PostImages.propTypes = {
  images: PropTypes.arrayOf(PropTypes.object),
};

export default PostImages;
